/*  Mouseover overlay stream info  */
/* Title */
div[class^="player-streaminfo__title qa-stream-title"] {
    /*background-color: black;*/
    font-size: 16px;
    font-weight: bold;
    text-shadow:
     2px 2px 0 #000,
     -1px -1px 0 #000,
     1px -1px 0 #000,
     -1px 1px 0 #000,
     1px 1px 0 #000;
}
/* "playing <game> for <#> viewers" */
div[class^="player-streaminfo__viewers"] {
    /*background-color: black;*/
    font-size: 16px;
    font-weight: bold;
    text-shadow:
     2px 2px 0 #000,
     -1px -1px 0 #000,
     1px -1px 0 #000,
     -1px 1px 0 #000,
