}

/* Remove black gradient overlay on mouseover */
div[class*="pl-controls-top js-controls-top"],
div[class*="pl-controls-bottom pl-flex qa-controls-bottom"] {
    background: none !important;
}

/* (VOD+Live) Hide "Chat on Videos" fixed chat header */
div[class*="tw-align-items-center tw-flex tw-flex-shrink-0 tw-justify-content-center video-chat__header"],
div[class*="room-selector__header tw-align-items-center tw-border-b tw-border-l tw-border-r tw-c-background-alt-2 tw-flex tw-flex-shrink-0 tw-full-width tw-justify-content-between tw-pd-l-2 tw-pd-r-1"] {
    display: none !important;
}

/* Hide "Get Bits" in the site title bar */
div[class="top-nav__menu tw-align-items-stretch tw-flex tw-flex-nowrap tw-full-height"] div[class*="tw-align-self-center tw-flex-grow-0 tw-flex-nowrap tw-flex-shrink-0 tw-mg-x-1"] {
    display: none !important;
}

/* Remove pinned top cheers at top of chat */
div[class="tw-z-default"] div[class="pinned-cheer-v2 tw-z-default"],
div[class="room-selector__header tw-align-items-center tw-border-b tw-border-l tw-border-r tw-c-background-alt-2 tw-flex tw-flex-shrink-0 tw-full-width tw-justify-content-between tw-pd-l-2 tw-pd-r-1"] {
    display: none !important;
}

/* Remove "Join the Twitch community! Discover the best live streams anywhere." in the side nav */
div[class="simplebar-content"] div[class="side-nav-page-info"] {
    display: none !important;
}
/* Remove "Recommended" from the side nav */
div[class="simplebar-content"] div[class="tw-full-width"] {
    display: none !important;
}

/* Remove part of the game directory's banner of unecessary space */
div[class="tw-overflow-hidden tw-relative"] {
    display: none !important;
}
/* Fix game directory image cutoff */
div[class="tw-card-img tw-card-img--size-8 tw-flex-shrink-0 tw-overflow-hidden"] {
    margin-bottom: -8px;
}
.tw-card-img--size-8 {
    width: 35px;
}
/* Move links due to directory game image resize */
div[class="directory-tabs"] {
    background-color: #fff;
    border-bottom: 1px solid #dad8de;
    height: 5rem;
    padding-left: 7.5rem; /* Custom line*/
}
/* Remove odd image border */
div[class="directory-header__avatar tw-absolute tw-bottom-0 tw-mg-b-1 tw-mg-l-3"] {
    border: 0;
}

/* Remove chat input box border on all but top sides */
.tw-theme--dark .tw-textarea {
    border: 0px solid #392e5c !important;
    border-top: 1px solid #392e5c !important;
    border-bottom: 1px solid #392e5c !important;
    background: #0e0c13;
    color: #faf9fa;
}
/* Remove chat focus box highlight */
.tw-textarea:focus, .tw-theme--dark .tw-textarea:focus, .tw-theme--light .tw-textarea:focus {
    box-shadow: none !important;
    border: 0px solid red;
}
.tw-textarea, .tw-theme--dark .tw-textarea, .tw-theme--light .tw-textarea {
    border: 0px solid red;
}
/* Chat offset */
textarea[class="tw-block tw-border-radius-medium tw-font-size-6 tw-full-width tw-textarea tw-textarea--no-resize"] {
    padding-bottom: 0px !important;
    margin-bottom: 40px !important;
}
/* Miniature chat */
div[class="chat-input tw-block tw-pd-b-2 tw-pd-x-2"] {
    padding-left: 0px !important;
    padding-right: 0px !important;
    padding-bottom: 0px !important;
}
div[class="tw-relative tw-z-default"] {
    margin-bottom: -39px !important;
}
/* Remove "Chat" purple button */
div[class="chat-input__buttons-container tw-flex tw-justify-content-between tw-mg-t-1"] button[class="tw-interactive tw-button"] {
    display: none !important;
}
/* Shift gear icon and show chat users list button to the right side */
div[class="tw-flex tw-flex-row"] {
    z-index: 1 !important;
}
svg[class="tw-svg__asset tw-svg__asset--gear tw-svg__asset--inherit"], 
svg[class="tw-svg__asset tw-svg__asset--viewerlist tw-svg__asset--inherit"] {
    width: 14px;
    height: 14px;
}
/* Emoticon icon chat menu */
svg[class="tw-svg__asset tw-svg__asset--emoticons tw-svg__asset--inherit"] {
    width: 16px;
    height: 16px;
}
button[class="tw-interactive tw-button-icon tw-button-icon--secondary"] {
    margin-left: -284px !important;
    margin-bottom: -94px !important;
}
/* :hover icon background */
.tw-button-icon__icon {
    display: inline-flex;
    padding-left: 6px;
    padding-right: 6px;
}

