/* Remove extensions video overlay*/
div[class="extension-container"],
div[class="extension-taskbar"]{
    display: none !important;
}

/* Remove Overwatch Events in the game's directory */
div[class="tw-animation tw-animation--animate tw-animation--duration-medium tw-animation--fill-mode-both tw-animation--slide-in-right tw-animation--timing-ease"] {
    display: none !important;
}
div[class="tw-pd-b-3 tw-pd-t-2 tw-pd-x-3"] {
    padding-right: 0 !important;
}

/* Remove "Block" and "Hide Thumbnails" FFZ buttons in a game's directory */
button[class="tw-mg-l-1 tw-button ffz-directory-toggle-block"], 
button[class="tw-mg-l-1 tw-button ffz-directory-toggle-thumbnail"] {
    display: none !important;
}

/* Remove Overwatch's Hero used by streamer */
div[class="preview-card-appendage tw-flex"] {
    display: none !important;
}

/* Remove "Online Friends" in the side nav */
div[class="tw-flex-grow-1 tw-full-width"] 
div[class="tw-mg-b-2"] {
    display: none !important;
}

/* Remove "Followed Channels" Title in the side nav (+collapsed) */
div[class="side-nav-header tw-mg-t-1 tw-mg-x-1 tw-pd-t-1"], 
div[class="side-nav-header tw-align-items-center tw-c-text-alt-2 tw-flex tw-flex-wrap tw-justify-content-center tw-mg-t-1 tw-pd-b-05 tw-pd-t-1"] {
    display: none !important;
}

/* Remove "Search to Add Friends" search bar in the side nav */
div[class="side-nav__theme-wrapper tw-border-r tw-flex tw-flex-column tw-flex-grow-1 tw-flex-nowrap tw-flex-shrink-0 tw-full-height tw-full-width tw-relative"] 
div[class="tw-border-t tw-pd-1"] {
    display: none !important;
}

/* Remove chat rules pop-up */
div[class="tw-pd-x-2"] div[class="chat-rules tw-relative"] {
    display: none !important;
}

/* Shrink side nav width */
div[class="tw-flex tw-flex-nowrap tw-full-height tw-overflow-hidden tw-relative"] div[class*="side-nav tw-flex-shrink-0 tw-full-height tw-relative"] {
    width: 200px !important;
}

/* Remove Chromecast button on players */
div[class="player-buttons-right"] button[class="player-button"] {
    display: none !important;
}

/* Remove "It's your 2 month anniversary" pop-up at the top of chat */
div[class*="chat-resub-notification tw-absolute tw-left-0 tw-pd-0 tw-right-0 tw-top-0"] div[class*="chat-share-prompt tw-absolute tw-full-width tw-pd-l-05 tw-pd-y-05"] {
    display: none !important;
}

