div[data-story_category^="4"] {
    display: none !important;
}

/* Hide Log In to Instagram footer */
section[class*="bR_3v Fzijm"] {
    display: none !important;
}

/* Remove sign up banner */
section[class*="infobar listingsignupbar"],
section[class*="infobar commentsignupbar"]
{
    display: none !important;
}

/* Hide sidebar if width <768px */
@media (max-width: 768px) {
    .side {
        display: none !important;
    }
}

/* Remove post links */
li[class="share"], 
li[class="give-gold-button"], 
li[class*="report-button"], 
li[class="crosspost-button"], 
span[class="redditSingleClick"], 
form[class="state-button hide-button"] {
    display: none !important;
}

/* Remove the fixed content when scrolling down to comments  */
.pinnable-content.pinned {
    display: none;
}

/* Hide all child comments button */
a[class^="noCtrlF res-toggleAllChildren"] {
	-moz-box-shadow:inset 0px 1px 0px 0px #f5978e;
	-webkit-box-shadow:inset 0px 1px 0px 0px #f5978e;
	box-shadow:inset 0px 1px 0px 0px #f5978e;
	background:-webkit-gradient(linear, left top, left bottom, color-stop(0.05, #f24537), color-stop(1, #c62d1f));
	background:-moz-linear-gradient(top, #f24537 5%, #c62d1f 100%);
	background:-webkit-linear-gradient(top, #f24537 5%, #c62d1f 100%);
	background:-o-linear-gradient(top, #f24537 5%, #c62d1f 100%);
	background:-ms-linear-gradient(top, #f24537 5%, #c62d1f 100%);
	background:linear-gradient(to bottom, #f24537 5%, #c62d1f 100%);
	filter:progid:DXImageTransform.Microsoft.gradient(startColorstr='#f24537', endColorstr='#c62d1f',GradientType=0);
	background-color:#f24537;
	-moz-border-radius:6px;
	-webkit-border-radius:6px;
	border-radius:6px;
	border:1px solid #d02718;
	display:inline-block;
	cursor:pointer;
	color:#ffffff !important;
	font-family:Arial;
	font-size:15px;
	font-weight:bold;
	padding:6px 24px !important;
	text-decoration:none;
	text-shadow:0px 1px 0px #810e05;
}
a[class^="noCtrlF res-toggleAllChildren"]n:hover {
	background:-webkit-gradient(linear, left top, left bottom, color-stop(0.05, #c62d1f), color-stop(1, #f24537));
	background:-moz-linear-gradient(top, #c62d1f 5%, #f24537 100%);
	background:-webkit-linear-gradient(top, #c62d1f 5%, #f24537 100%);
	background:-o-linear-gradient(top, #c62d1f 5%, #f24537 100%);
	background:-ms-linear-gradient(top, #c62d1f 5%, #f24537 100%);
	background:linear-gradient(to bottom, #c62d1f 5%, #f24537 100%);
	filter:progid:DXImageTransform.Microsoft.gradient(startColorstr='#c62d1f', endColorstr='#f24537',GradientType=0);
	background-color:#c62d1f;
}
a[class^="noCtrlF res-toggleAllChildren"]:active {
	position:relative;
	top:1px;
}

/* Hide Reddit user shittymorph's Reddit comments and replies to his comment */
div[data-author-fullname^="t2_tfpcq"] div[class^="entry unvoted"] form[class^="usertext warn-on-unload"] div[class^="usertext-body may-blank-within md-container"] div[class^="md"] p {
    display: none !important;
}
/* Highlights a red bar on the username line to further distinguish Shittymorph comments */
div[data-author-fullname^="t2_tfpcq"] div[class^="entry unvoted"] p[class^="tagline"] {
    background-color: red;
    color: white;
}

/* Night mode */
.c-seven-up {
    background: white;
    margin: 0 auto;
}

div[class="l-root l-reskin"] {
    background: white;
}

.c-compact-river__entry {
    background-color: white;
    border-top: 1px solid black;
    margin-bottom: 8px;
}

/* Night mode on main stories */
.c-seven-up .c-entry-box--compact {
    background-color: white;
    color: black;
	margin-bottom: 1px;
	padding: 8px;
}

/* Remove live chat user photo */
#author-photo {
    display: none !important;
}

