/* Like bar */
ytd-sentiment-bar-renderer[id*="sentiment"] div[id*="container"] {
    background-color: red !important
}

div[id*="container"] div[id*="like-bar"] {
    background-color: #3EA6FF !important;
}

/* Static chat usernames */
#author-name {
    color: rgb(255, 0, 0) !important;
    font-weight: bold !important;
    font-family: Verdana !important;
}

/* Enable dark mode on search bar suggestions */
.sbsb_a {
    background-color: #272822 !important;
}
/* Keyboard scrolling has different highlight color */
.sbsb_d {
    background-color: #49483E !important;
}

/* Autocomplete search results text */
.sbsb_a ul li {
    color: #F8F8F2 !important;
}
.sbsb_a ul li:hover {
    background-color: #49483E !important;
}

/* Background border fix for "Report search predictions" on autocomplete search results */
.sbfl_a, .sbfl_b {
    background-color: #272822 !important;
}
.sbfl_a, .sbfl_b:hover {
    background-color: #49483E !important;
}
li[class*="sbsb_c gsfs"]:last-child {
    background-color: #272822 !important;
}
li[class*="sbsb_c gsfs"]:last-child:hover {
    background-color: #49483E !important;
}
/* "Report search predictions" text */
.sbfl_b:hover {
    color: #F8F8F2 !important;
}

