div[class="paged_list_wrapper"] div[class="logged_out_related_questions_container"] {
    display: none !important;
}

/* Remove Ask your own question static pop-up */
div[class="fixable_clone"] div[class="BelowQuestionAddPrompt fixable_fixed"] {
    display: none !important;
}

